import React,{useState} from 'react'
import '../../src/scripts/jsfx'
import jsfx from '../../src/scripts/jsfx'


export default function InsideModifier({title,list}) {

  const FuncList=['unoise','sine','saw','traingle','square','synth','noise','string']

  let chunks = [];
  for (let i = 0; i < FuncList.length; i += 2) {
    chunks.push(FuncList.slice(i, i + 2));
  }

  let FuncItem = chunks.map((chunk, rowIndex) => (
    <tr key={rowIndex} style={{fontSize:12}}>
      {chunk.map((item, colIndex) => (
        <td className='chuncks' key={colIndex}>
            <input type='radio' style={{alignItems:'left'}} name='fngroup' value={item}/>
            <label>{item}
            </label>
            </td>
      ))}
    </tr>
  ));
  // const getParamValues = (paramName, type) => {
  //   return jsfx.Module[paramName]?.
  // };
  // const getRangeValue = (paramName) => {
  //   const paramValues = getParamValues(paramName);
  //   if (!paramValues) return null;
    
  //   return {
  //     min: paramValues.Min.L,
  //     max: paramValues.Max.H,
  //     defaultValue: paramValues.Start.D,
  //   };
  // };
  const [value, setValue] = useState(jsfx.Module[title]["params"][item].D);

// useEffect to handle side effects
useEffect(() => {
  window.ModifyValue({ title: title }, { item: item }, parseFloat(value));
}, [value, title, item]);

  let listitem = list.map((item,index)=>{
    if(item==="Func"){
      return(
        <tr>
        <td key={index}>{item}</td>
        <td style={{textAlign:'right'}}>
          <div class="radio-group">
          <form className='form'>
            {FuncItem}
          </form>
          </div>
          </td>
        </tr>
      );
    }
    else{
      if(jsfx.Module[title]["params"][item]){
        return(
          <tr>
            <td key={index}>{item}</td>
            <td style={{textAlign:'right'}}>
              <input 
                type='range'
                id = {title +"$"+item}  
                min={jsfx.Module[title]["params"][item].L} 
                max={jsfx.Module[title]["params"][item].H} 
                step={(jsfx.Module[title]["params"][item].H - jsfx.Module[title]["params"][item].L) > 10 ? 1 : 0.01} 
                style={{width:170}} 
                name={item}
                value={value}
                onChange={(ev) => setValue(ev.target.value)}
                >
              </input>
              </td>
          </tr>
          );
      }
      else{
        return(
          <tr>
            <td key={index}>{item}</td>
            <td style={{textAlign:'right'}}>
              <input 
                type='range' 
                min={0} 
                max={1} 
                step={0.01} 
                style={{width:170}} 
                name={item}
                defaultValue={0}>
              </input>
              </td>
          </tr>
          );
      }
    }
})

  return (
    <div className='insideModifier'>
      <div style={{paddingLeft:6,paddingRight:6}}><b>{title}</b></div>
      <div>
        <table id='tablewidth'>
          {listitem}
        </table>
      </div>
      <div className='rangeInput'>
        
      </div>
    </div>
  )
}
