import React, { useState } from 'react';

export default function Range() {
  const [values, setValues] = useState([3, 3]); // Initial values for range inputs

  const handleChange = (index, newValue) => {
    const newValues = [...values]; // Create a copy of the values array
    newValues[index] = newValue; // Update the value at the specified index
    setValues(newValues); // Update the state with the new values
  };

  return (
    <div>
      <form>
        {values.map((value, index) => (
          <div key={index}>
            <input
              type='range'
              name={`range${index}`}
              min={0}
              max={50}
              step={1}
              value={value}
              onChange={(ev) => handleChange(index, parseInt(ev.target.value))}
            />
            <label>this is input {index + 1}</label>
          </div>
        ))}
      </form>
    </div>
  );
}
