import React from 'react'



export default function InsideModifier({title,list}) {


  const PresetList=['Random','Coin','Sound3','Sound4','Sound5','Sound6']
  const FuncList=['unoise','sine','saw','traingle','square','synth','noise','string']
  
  let soundButton;
  if(title === 'Presets'){
    soundButton = PresetList.map((preset,index)=>(
      <button key={index} style={{width:81,margin:3}}>{preset}</button>
    ));
  }

  let FuncItem = FuncList.map((item,index)=>(
    <td><button key={index}>{item}</button></td>
  ))

  let listitem;
  
  listitem = list.map((item,index)=>{
    if(item === 'Func'){
      [0,1,2,3].map(rowIndex => (
        <tr key={rowIndex}>
          {[0,1].map(cellIndex => {
            const itemIndex = rowIndex*2+cellIndex;
            const Funcitem = FuncList[itemIndex];
              return(
                <tr>
                <td key={index}>{item}</td>
                <td style={{textAlign:'right'}}>
                  <table align='right'>
                    <td>{Funcitem}</td>
                  </table></td>
                </tr>
              );
            })}
            </tr>
      ))}
    else{
      return(
      <tr>
        <td key={index}>{item}</td>
        <td style={{textAlign:'right'}}><input type='range' min={0} max={1} step={0.01} style={{width:170}}></input></td>
      </tr>
      );
    }
})

  return (
    <div className='insideModifier'>
      <div style={{paddingLeft:6,paddingRight:6}}><b>{title}</b></div>
      <div>
        <table id='tablewidth'>
          {listitem}
        </table>
      </div>
      <div className='rangeInput'>
        {soundButton}
      </div>
    </div>
  )
}
