import React from 'react';
import jsfx from '../scripts/jsfx';

const FuncList = ['unoise', 'sine', 'saw', 'triangle', 'square', 'synth', 'noise', 'string'];

const FuncComponent = ({ title, chunks }) => {
  const renderFuncItems = () => {
    return Object.keys(jsfx.G).map((chunk, rowIndex) => (
      <tr key={rowIndex} style={{ fontSize: 12 }}>
        {chunk.map((item, colIndex) => (
          <td className='chunks' key={colIndex}>
            <input
              type='radio'
              style={{ alignItems: 'left' }}
              name='fngroup'
              value={item}
              onChange={(ev) => {
                window.ModifyValue(title, item, ev.target.value);
              }}
            />
            <label>{item}</label>
          </td>
        ))}
      </tr>
    ));
  };

  return (
    <table>
      <tbody>
        {renderFuncItems()}
      </tbody>
    </table>
  );
};

export default FuncComponent;
